import React, { Component } from 'react';
import ResultTable from './component/ResultTable';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import './App.css';

class App extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch(`/api/world?name=${encodeURIComponent(this.state.post)}`);
    const body = await response.text();

    this.setState({ responseToPost: JSON.parse(body) });
  };

render() {
  const options = [
    'Full Name', 'Country Name', 'Code'
  ];
  const defaultOption = options[0];

    return (
      <div className="App">
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Search for countries:</strong>
          </p>
          <div>
            <Dropdown className='dropDown'
                      placeholderClassName='placeholderDropdown'
                      options={options}
                      onChange={this._onSelect} 
                      value={defaultOption} 
                      placeholder="Select an options" />
            <input
              type="text"
              value={this.state.post}
              onChange={e => this.setState({ post: e.target.value })}
            />
            <button type="submit">Submit</button>
          </div>
        </form>
        <ResultTable data={this.state}/>
      </div>
    );
  }
}

export default App;
